mapper.select:
SELECT *
from timeSlot
where timeSlotDate >= #{startDate}
  and timeSlotDate <= #{endDate}
order by slot

mapper.update:
update timeSlot
set timeSlotStatus = #{timeSlotStatus}
where timeSlotId = #{timeSlotId}

mapper.select:
select *
from timeSlot
where timeSlotDate = #{date}
  and slot = #{slot}
