mapper.update:
update user
set userPassword = #{password}
where userId = #{userId}

update user
set userAccountStatus = #{userAccountStatus}
where userId = #{userId}

update user
set firstName = #{firstName},
lastName = #{lastName}
where userId = #{userId}

mapper.select:
select *
from user
where userName = #{userName}
  and userPassword = #{userPassword}
ORDER BY userId DESC limit 1
